#include "Random.h"#include <stdlib.h>#include <time.h>//Seed with the time (a random seed) and return the time used to seed.unsigned long SeedRandom(){	unsigned long theTime = time(NULL);	srand(theTime);	return theTime;}//Seed with an explicit seed.void SeedRandom(int val){	srand(val);}//Return an int in the range (0 - range].  range must be (0 - 32768).int RandZeroInt(int range){	return rand() % range;}//Return an int in the range (-range/2 - range/2).  range must be (0 - 32768).int RandNegInt(int range){	if (range % 2 == 0)		return 0;	//if the range isn't odd, this algorithm doesn't really work.	return rand() % range - range / 2;}//Return a float in the range (0 - range).float RandZeroFloat(float range){	return (float)(rand()) / ((float)RAND_MAX / range);}//Return a float in the range (-range - range).float RandNegFloat(float range){	return ((float)(rand()) / ((float)RAND_MAX / range) - range / 2.0);}